<script>
export default {
  data() {
    return {
      scoreone: 0,
      scoretwo:0,
      playing: false,
      playerChoice: "",
      computerChoice: "",
      result: "",
      gameOver: false,
      heartchoice:"src/img/heart.png"
    };
  },
  methods: {
    playGame() {
      const choices = ["https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcShmfbi1RzFZMuQWF1njLKYTKPTDj7h3h5b6g&usqp=CAU", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQSwpSgOOXyv_e4kq_oD709_t6VKeukBUZnNb-QuLheEBq3BtUaZKQ-_SsRAg-ZjeXgmHM&usqp=CAU", "data:image/jpeg;base64,","data:image/jpeg;base64,"];
      this.playerChoice = choices[Math.floor(Math.random() * choices.length)];
      this.computerChoice = choices[Math.floor(Math.random() * choices.length)];
      this.calculateResult();
      this.playing = true;
    },
    calculateResult() {
      if (this.playerChoice === this.computerChoice) {
        this.result = "เสมอ";
      }else if(this.playerChoice === "data:image/jpeg;base64,"){
        this.result = "playerOne WIN";
        this.scoreone += 1;
      }
      else if(this.computerChoice === "data:image/jpeg;base64,"){
        this.result = "playerTwo WIN";
        this.scoretwo += 1;
      }
      else if (
        (this.playerChoice === "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcShmfbi1RzFZMuQWF1njLKYTKPTDj7h3h5b6g&usqp=CAU" && this.computerChoice === "data:image/jpeg;base64,") ||
        (this.playerChoice === "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQSwpSgOOXyv_e4kq_oD709_t6VKeukBUZnNb-QuLheEBq3BtUaZKQ-_SsRAg-ZjeXgmHM&usqp=CAU" && this.computerChoice === "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcShmfbi1RzFZMuQWF1njLKYTKPTDj7h3h5b6g&usqp=CAU") ||
        (this.playerChoice === "data:image/jpeg;base64," && this.computerChoice === "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQSwpSgOOXyv_e4kq_oD709_t6VKeukBUZnNb-QuLheEBq3BtUaZKQ-_SsRAg-ZjeXgmHM&usqp=CAU")
      ) {
        this.result = "playerOne WIN";
        this.scoreone += 1;
        this.scoretwo += 0;
      } else {
        this.result = "playerTwo WIN";
        this.scoreone += 0;
        this.scoretwo += 1;
      }
    },
    resetGame() {
      this.playing = false;
      this.playerChoice = "";
      this.computerChoice = "";
      this.result = "";
      this.gameOver = false;
      this.scoreone=0;
      this.scoretwo=0;
    },
  },
};
</script>
<template>
  <div>
    <h1>ฉุบ ฉุบ</h1>
    <div class="row" v-if="playing">
  <div class="col-sm-6 mb-3 mb-sm-0">
    <div class="card">
      <div class="card-body" style="color: rgb(0, 147, 169);">
        <h5 class="card-title">playerOne</h5>
        <p>scoreplayerOne: {{ scoreone }}</p>
        <h2>playerOne: <img :src="playerChoice" style="width: 100px;" alt="player one Choice"></h2>
      </div>
    </div>
  </div>
  <div class="col-sm-6">
    <div class="card">
      <div class="card-body" style="color: rgb(0, 147, 169);">
        <h5 class="card-title">playerTwo</h5>
        <p>scoreplayerTwo: {{ scoretwo }}</p>
        <h2>playerTwo:<img :src="computerChoice" style="width: 100px;" alt="player two Choice"> </h2>
        </div>
    </div>
  </div>
  <h3>ผล: {{ result }}</h3>
  </div>
    <button @click="playGame" :disabled="gameOver">เริ่มเล่น</button>
    <button @click="resetGame" >เริ่มใหม่</button>
    
    </div>
  
  
</template>




